"use strict";document.querySelector(".flip-container").onclick=function(){var e=document.querySelector(".flip-container");-1===e.className.indexOf("do-flip")?e.className="flip-container do-flip":e.className="flip-container"};for(var dots=document.querySelectorAll(".dot"),i=0;i<dots.length;i++)dots[i].onmouseover=onMouseOverDot,dots[i].onmouseleave=onMouseLeave;function onMouseOverDot(){var e=this.id;document.querySelector(".top").classList+=" "+e,document.getElementById("headshot").src="img/headshot-"+e+".png"}function onMouseLeave(){document.querySelector(".top").classList="top",document.getElementById("headshot").src="img/headshot.png"}